var link=document.querySelector(".hotel-search-btn"),popup=document.querySelector(".search-form"),datein=popup.querySelector("[name=datein]"),dateout=popup.querySelector("[name=dateout]"),form=popup.querySelector(".popup-form");link.addEventListener("click",function(e){e.preventDefault(),popup.classList.add("search-form-show"),datein.focus()}),form.addEventListener("submit",function(e){datein.value&&dateout.value||(e.preventDefault(),popup.classList.remove("modal-error"),popup.classList.add("modal-error"))}),window.addEventListener("keydown",function(e){27==e.keyCode&&popup.classList.contains("search-form-show")&&popup.classList.remove("search-form-show")});